definition.script:
#!groovy

def PROJECT_NAME = "cucumber-test-automation"
def IMAGE_NAME = "mvn_project:latest"
    pipeline {
        agent {
            dockerfile {
                 args '-v $HOME/.m2:/root/.m2'
                 args '-v $(pwd)/reports:/automation/reports'

             }
        }

        stages {
            stage('Compile') {
                steps {
                    sh 'mvn compile -Dmaven.repo.local=/root/.m2'
                }
            }
            stage("UI Validation Tests") {
                  steps {
                    script {
                      sh 'mvn clean test -Dmaven.repo.local=/root/.m2 -Dcucumber.options="src/test/java/features --tags \'@SmokeTest\' --glue steps"'
                    }
                  }
                }
        }

    }
